from svgpathtools import Path, parse_path

# Compute bounding box manually (since svgpathtools.bbox doesn't exist)
def get_path_bbox(path):
    xs = []
    ys = []
    for segment in path:
        pts = [segment.start, segment.end]
        if hasattr(segment, 'control1'):
            pts.append(segment.control1)
        if hasattr(segment, 'control2'):
            pts.append(segment.control2)
        xs.extend(p.real for p in pts)
        ys.extend(p.imag for p in pts)
    return min(xs), min(ys), max(xs), max(ys)

def scale_and_translate_path(replacement_path, target_bbox):
    # Get bounding boxes
    rx0, ry0, rx1, ry1 = get_path_bbox(replacement_path)
    tx0, ty0, tx1, ty1 = target_bbox

    # Dimensions
    rw, rh = rx1 - rx0, ry1 - ry0
    tw, th = tx1 - tx0, ty1 - ty0

    # Scale factor (uniform)
    scale = min(tw / rw, th / rh)

    # Scale replacement path
    scaled_path = replacement_path.scaled(scale)

    # New bbox after scaling
    sx0, sy0, _, _ = get_path_bbox(scaled_path)

    # Translate so top-left aligns
    dx = tx0 - sx0
    dy = ty0 - sy0

    # Apply translation
    translated_path = scaled_path.translated(complex(dx, dy))

    return translated_path

